

public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=21,b=31;
		System.out.println("before swapping");
		System.out.println("a="+a+" "+"b="+b);
		 a=a+b;
		 b=a-b;
		 a=a-b;
		 System.out.println("after swapping");
		 System.out.println("a="+a+" "+"b="+b);
	}

}
